INSERT INTO `saal_events_et` VALUES (190,'','<p>Koost&ouml;&ouml;s Von Krahli Teatri, Kristian Smedsi ja Tampere &Uuml;likooli Teatrirakenduse Uurimiskeskusega toimub Tartu &Uuml;likooli Viljandi Kultuuriakadeemia teatrikunsti 3. kursuse tudengitel aprilli alguses kahen&auml;dalane workshop Tamperes. Workshop on ettevalmistuseks 2011. aastal valmivale lavastusele Dostojevski romaani &bdquo;Vennad Karamazovid\" ainetel. Workshopi viib l&auml;bi Kristian Smeds ja tulemusi esitletakse demo-etendusel Kanuti Gildi SAALis.</p>\r\n<p>&nbsp;</p>\r\n<p>HYPEKAS on Kanuti Gildi SAALi l&uuml;hit&ouml;&ouml;de sari, kus oma ideid proovivad noored koreograafid ja muidu julged kunstnikud. Sarja toetavad criticaldance.com ja Eesti Kultuurkapital.</p>\r\n<hr />\r\n<p>&nbsp;</p>\r\n<p>Team: <strong>T&Uuml; Viljandi Kultuuriakadeemia III kursuse teatrikunsti tudengid, Kristian Smeds</strong> /FIN/<br />Foto: <strong>Marion Undusk</strong><br />Toetajad: <strong>Tamperen Yliopiston tutkivan teatterity&ouml;n keskus, Nordplus, Eesti Kultuurkapital, Tallink, Temps D&acute;Images</strong></p>\r\n<p>&nbsp;</p>',1,'','<p>HYPEKAS:</p>\r\n<p><span class=\"suur\">T&Uuml; VKA 8. LEND &amp; KRISTIAN SMEDS</span></p>\r\n<p class=\"keskmine\">&bdquo;Karamazov work in progress\"</p>');
INSERT INTO `saal_events_et` VALUES (191,'','<p>VII Urbanistika ja linnamaastike p&auml;evad toovad kokku nii urbanistika, arhitektuuri, maastikuarhitektuuri, disaini ja inseneriteaduse rahvusvahelised eksperdid, kui ka aktivistid ja sotsiaalse suunitlusega ettev&otilde;tjad. Sel aastal keskendub konverents t&auml;navale kui sotsiaalsele ruumile, kus kohtuvad nii inimesed, tegevused ja lood, kui ka infrastruktuur ja linnadisain.</p>\r\n<p>&nbsp;</p>\r\n<p>Programm koosneb kutsutud esinejatega loengutest, millega paralleelselt toimuvad temaatiliselt grupeeritud l&uuml;hiettekanded, vestlusringid ja taktikalised workshopid Tallinnas ja selle &uuml;mbruses. Eraldi blokk on p&uuml;hendatud EKA Arhitektuuriosakonna &uuml;li&otilde;pilast&ouml;&ouml;dele ja koost&ouml;&ouml;projektile TL&Uuml; linnakorralduse tudengitega. Peamiseks s&uuml;ndmuspaigaks on Kanuti Gildi SAAL, paralleelsed sessioonid toimuvad ka EKA Arhitektuuriosakonnas. Konverentsi keeleks on inglise keel.</p>\r\n<p><br />Kutsutud esinejateks on arhitekt <strong>Henning Thomsen</strong> Gehl Arhitektidest /DEN/, <strong>Dr Sabine Knierbein</strong> Viini Tehnika&uuml;likoolist, professor, <strong>Dr Dago Antov</strong> Tallinna Tehnika&uuml;likoolist, professor, <strong>Dr Zane Berzina</strong> Berliin-Weissensee Kunstikolledzist.</p>\r\n<hr />\r\n<p>Korraldajad: <strong>Eesti Kunstiakadeemia Urbanistika ja Linnamaastike &otilde;ppetoolid</strong><br />Korraldusmeeskond: <strong>Katrin Koov, Panu Lehtovuori, Lilia del Rio, J&uuml;ri Soolep</strong><br />Sissep&auml;&auml;s tasuta! <br /><a href=\"http://www.urbanistika.ee/news\" target=\"_blank\">www.urbanistika.ee/news</a></p>\r\n<p>&nbsp;</p>',1,'','<p>EESTI KUNSTIAKADEEMIA:<br /><span class=\"keskmine\">VII URBANISTIKA JA LINNAMAASTIKE P&Auml;EVAD</span> \"Social Street\"</p>');
