INSERT INTO `saal_texts_en` VALUES (1,'ON TOUR:','<p>Kanuti Gildi SAAL productions on tour 2010:</p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"text-decoration: underline;\">MAY:</span><br />31.05 / 17:30 <strong>united dancers of ZUGA</strong> \"Zuga zuug zuh-zuhz-zuh\" <a href=\"http://assitej.ee/nb2011/nbfestival.html\" target=\"_blank\">NB Festival 2011</a>, Theatre No99 cafe, Tallinn /EST/</p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"text-decoration: underline;\">APRIL:</span><br />26.04 / 19:00 <strong>Sandra Z</strong> \"Autists/Artists\" <a href=\"http://www.genklubi.ee/\" target=\"_blank\">Genialistide Klubi</a>, Tartu /EST/</p>\r\n<p>23.04 / 18:00 <strong>ALEXIS STEEVES &amp; RAIN SAUKAS</strong> &bdquo;How quickly these accidents\" <a href=\"http://tbounce.net/est.html\" target=\"_blank\">contemporary dance festival \"Bounce\"</a>, J&otilde;hvi Kontserdimaja Kammersaal, J&otilde;hvi /EST/</p>\r\n<p>09.04 / 20.00 <strong>Sandra Z</strong> \" Autists/Artists\" <a href=\"http://www.uustants10.tantsuliit.ee/\" target=\"_blank\">festival UUS TANTS 10</a>, Haapsalu Cultural Centre, big hall, Haapsalu /EST/<br />09.04 / 19:00 <strong>HENRI H&Uuml;TT</strong> \"The Reader\" <a href=\"http://www.uustants10.tantsuliit.ee/\" target=\"_blank\">festival UUS TANTS 10</a>, Haapsalu Cultural Centre, small hall, Haapsalu /EST/<br />09.04 / 11:00+13:00 <strong>united dancers of ZUGA</strong> \"Zuga zuug zuh-zuhz-zuh\" <a href=\"http://www.uustants10.tantsuliit.ee/\" target=\"_blank\">festival UUS TANTS 10</a>, Iloni Imedemaa, Haapsalu /EST/<br />08.04 / 19:15 <strong>united dancers of ZUGA</strong> \"Old Buddies\" <a href=\"http://www.uustants10.tantsuliit.ee/\" target=\"_blank\">festival UUS TANTS 10</a>, Haapsalu Cultural Centre, big hall, Haapsalu /EST/<br />07.04 / 20:00 <strong>J&Uuml;RI NAEL </strong>\"Waiting For...\" <a href=\"http://www.uustants10.tantsuliit.ee/\" target=\"_blank\">festival UUS TANTS 10</a>, Haapsalu Cultural Centre, Haapsalu /EST/<br />07.04 / 16:30 <stron