INSERT INTO `saal_texts_en` VALUES (17,'BECOME A VOLUNTEER','<p>Kanuti Gildi SAAL is looking for volunteers who are willing to sell and check tickets at the door, distribute promotion materials and help with other tasks concerning preparing performances.</p>\r\n<p>&nbsp;</p>\r\n<p>Kanuti Gildi SAAL offers a chance to experience the hidden side of producing performances and possibility to see the performances programmed here.</p>\r\n<p>&nbsp;</p>\r\n<p>Let us know of your interest: mairika @ tants.ee</p>\r\n<p>&nbsp;</p>');
