INSERT INTO `saal_texts_et` VALUES (11,'N?ITUSED 2009','<p>16.09.2009 - 31.12.2009 &bdquo;&bdquo;Estonia Meets Africa\" ehk vaade eemalt\" eri<br />GLEN projektis osalenud vabatahtlike r&auml;ndn&auml;itus. Vahelduvad foto ekspositsioonid vabatahtlike poolt erinevates maailma riikides kogetust-n&auml;htust.</p>\r\n<p>11.-14.11.2009 NU Performance III: Recycle Pop<br />Festivali erin&auml;itus. Kuraator: Anders H&auml;rm.</p>\r\n<p>01.12.2009 - 31.01.2010  Armsad inimesed ruumis<br />EKA stsenograafia 3. kursuse tudengid: Eve Ormisson, Pille Kose, Kristel Maam&auml;gi, Maarja Noormets, Kadri H&uuml;lp, Mairi Valgmaa.</p>\r\n<p>&nbsp;</p>');
INSERT INTO `saal_texts_et` VALUES (12,'N?ITUSED','<p>16.09.2009 - 31.12.2009 &bdquo;&bdquo;Estonia Meets Africa\" ehk vaade eemalt\" eri<br />GLEN projektis osalenud vabatahtlike r&auml;ndn&auml;itus. Vahelduvad foto ekspositsioonid vabatahtlike poolt erinevates maailma riikides kogetust-n&auml;htust.</p>\r\n<p>11.-14.11.2009 NU Performance III: Recycle Pop<br />Festivali erin&auml;itus. Kuraator: Anders H&auml;rm.</p>\r\n<p>01.12.2009 - 31.01.2010  Armsad inimesed ruumis<br />EKA stsenograafia 3. kursuse tudengid: Eve Ormisson, Pille Kose, Kristel Maam&auml;gi, Maarja Noormets, Kadri H&uuml;lp, Mairi Valgmaa.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>');
INSERT INTO `saal_texts_et` VALUES (13,'TUNNID','<p class=\"suur\">TUNNID Kanuti Gildi SAALi stuudios s&uuml;gis 2010 - kevad 2011:</p>\r\n<hr />\r\n<p class=\"keskmine\">T 9:30-11:00 + N 9:30-11:00 SUZUKI TREENING - KADI TUDRE</p>\r\n<p class=\"keskmine\">&nbsp;</p>\r\n<p class=\"keskmine\">K 10:00 5 R&Uuml;TMI TANTS - KRISTINA PASHKEVICIUS</p>\r\n<p class=\"keskmine\"><span style=\"text-decoration: underline;\">ei toimu alates 23.03.2011</span>!</p>\r\n<p class=\"keskmine\">&nbsp;</p>\r\n<p>Kanuti Gildi SAALi stuudios, Pikk 20 - sissep&auml;&auml;s Draakoni Galerii poolsest uksest, uksekella helistades.</p>\r\n<hr />\r\n<p><strong>Suzuki treening</strong> on m&otilde;eldud k&otilde;igile, kes tahavad arendada oma vaimset ja f&uuml;&uuml;silist kohalolu ning teadlikkust sellest. Treening on f&uuml;&uuml;silisel raske, koormus jalgade on suur. Eesm&auml;rgiks on arendada kehalist kontsentratsiooni, liikumise artikuleeritust ning erinevate h&auml;&auml;leresonaatorite ja artikulatsioonilihaste t&ouml;&ouml;d k&otilde;nelemisel.<br />S&uuml;steem on loodud jaapani lavastaja ja filosoofi Tadashi Suzuki poolt, kes oma ps&uuml;hhof&uuml;&uuml;silise treeningu loomiseks sai m&otilde;jutusi klassikalisest balletist, traditsioonilistest jaapani No ja Kabuki teatri n&auml;itlejatreeningutest ning idamaistest v&otilde;itluskunstidest. Treeningul keskendutakse alakeha (jalgade) tugevdamisele ja keha energiatsentrumi (puusapiirkond) nn. teadvusesse &auml;ratamisele ning liikumises ja k&otilde;nelemises tajuma ning kasutama hakkamisele. Tegeldakse trampimisega ja erinevate k&otilde;nnakutega, mille eesm&auml;rk on muuta labajalad tundlikuks (jalgadel, nende tundlikkusel ja kontakti tajumisel maaga on meie tasakaalus ja liikumises oluline roll t&auml;ita). &Otilde;pitakse erineva raskusastmega baasharjutusi, mida hiljem nii eraldi kui koos erinevate tekstidega (&uuml;ksi, paaris ja grupis) kasutatakse reaktsiooni kiiruse, t&auml;pse artikulatsiooni ja t&auml;helepanu treeninguks. Treeningu eesm&auml;rgiks on arendada lavalise tegevuse ps&uuml;hhof&uuml;&uuml;silist terviklikkust. Oodatud on k&otilde;ik, kel huvi!</p>\r\n<p>1 tund maksab 50 EEK (3.19 EUR).</p>\r\n<p>Kohtumiseni!<br />Kadi</p>\r\n<hr />\r\n<!--[if gte mso 9]><xml> <w:WordDocument> <w:View>Normal</w:View> <w:Zoom>0</w:Zoom> <w:HyphenationZone>21</w:HyphenationZone> <w:PunctuationKerning /> <w:ValidateAgainstSchemas /> <w:SaveIfXMLInvalid>false</w:SaveIfXMLInvalid> <w:IgnoreMixedContent>false</w:IgnoreMixedContent> <w:AlwaysShowPlaceholderText>false</w:AlwaysShowPlaceholderText> <w:Compatibility> <w:BreakWrappedTables /> <w:SnapToGridInCell /> <w:WrapTextWithPunct /> <w:UseAsianBreakRules /> <w:DontGrowAutofit /> </w:Compatibility> <w:BrowserLevel>MicrosoftInternetExplorer4</w:BrowserLevel> </w:WordDocument> </xml><![endif]--><!--[if gte mso 9]><xml> <w:LatentStyles DefLockedState=\"false\" LatentStyleCount=\"156\"> </w:LatentStyles> </xml><![endif]--><!--[if gte mso 10]> <mce:style><!   /* Style Definitions */  table.MsoNormalTable 	{mso-style-name:\"Table Normal\"; 	mso-tstyle-rowband-size:0; 	mso-tstyle-colband-size:0; 	mso-style-noshow:yes; 	mso-style-parent:\"\"; 	mso-padding-alt:0cm 5.4pt 0cm 5.4pt; 	mso-para-margin:0cm; 	mso-para-margin-bottom:.0001pt; 	mso-pagination:widow-orphan; 	font-size:10.0pt; 	font-family:\"Times New Roman\"; 	mso-ansi-language:#0400; 	mso-fareast-language:#0400; 	mso-bidi-language:#0400;} --> <!--[endif]-->\r\n<pre class=\"pisike\">Tere kolmap&auml;evased tantsijad!<br />Sel kevadel kolmap&auml;evaseid tantsutunde Kanutis minu juhendamisel <br />enam ei toimu alates 23.03.11. </pre>\r\n<pre class=\"pisike\">T&auml;nan k&otilde;iki, kes tundides tantsinud ja hommikud t&otilde;eliselt meeleolukaks aidanud luua.<br />Vaatame, mis uus hooaeg endaga kaasa toob. Seniks soovin ilusat kevade algust k&otilde;igile.</pre>\r\n<pre class=\"pisike\">5r&uuml;tmi tantse saab endiselt minu ja Rain Vainiku juhendamisel tantsida <br />teisip&auml;eviti kell 18.00-20.00 Telliskivi Loomelinnakus. <br />T&auml;psem info siit: <a href=\"http://tantsud.humare.ee/\">http://tantsud.humare.ee/</a></pre>\r\n<pre class=\"pisike\">Ps! Kui kellelgi on j&auml;&auml;nud ettemaksu tundide eest, <br />siis andke palun kindlasti teada, maksan teile r&otilde;&otilde;muga tagasi.</pre>\r\n<pre>Tantsimisteni,<span class=\"pisike\"><br />Kristina</span></pre>\r\n<hr />');
